-- --------------------------------------------------------------------------------------------
CREATE FUNCTION [dbo].[pims_IsUniqueProduct] (@prmCode nvarchar(20), @prmDesc nvarchar(200))
RETURNS nvarchar(1)
AS
BEGIN
  DECLARE @IsUnique nvarchar(1);
  SET     @IsUnique = 'Y';
  
--  IF (SELECT COUNT(*)
--      FROM   PIMS_PRODUCT) = 0
--    RETURN @IsUnique
  
  IF (SELECT COUNT(*)
      FROM   PIMS_PRODUCT
      WHERE  UPPER(TRIM(CODE) + TRIM(DESCRIPTION)) = UPPER(TRIM(@prmCode) + TRIM(@prmDesc))) > 0
    SET @IsUnique = 'N';
    
  RETURN @IsUnique;
END

GO
